/** * Created by yindi on 16/11/15. */app.directive('tempHeader',function(){    return {        restrict:'E',        template:                '<header class="header v-fm">' +                    '<i class="icon-arrow-l" ng-click="goHistory()"></i>' +                    '<p class="v-i1 tac">测试文字</p>' +                    '<i class="icon-home" ng-click="goHome()"></i>' +                '</header>',        replace:true,        link : function(scope, element, attrs) {            scope.goHistory = function(){                window.history.go(-1);            };            scope.goHome = function() {                window.location.href = 'http://www.baidu.com';            }        }    }});