/** * Created by yindi on 16/10/19. */var app = angular.module('routerApp',['ui.router']);    app.config(function($stateProvider,$urlRouterProvider){        $urlRouterProvider.otherwise('/index');        $stateProvider.state('index',{            url:'/index',            views:{                '':{                    templateUrl:'index.html'                },                'topbar@index':{                    templateUrl:'topbar.html'                },                'main@index':{                    templateUrl:'main.html'                }            }        })        .state('index.aside',{            url:'/aside',            views:{                'main@index':{                    templateUrl:'aside.html'/*                    controller:function($scope,$state){                        $scope.addUserType = function(){                            $state.go('index.aside.addusertype');                        }                    }*/                }            }        })            .state('index.aside.heightLevel',{                url:'/heightLevel',                templateUrl:'heightLevel.html'            })        ;    });